import React, { useState } from "react";
import PropTypes from "prop-types";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import CssBaseline from "@material-ui/core/CssBaseline";
import useScrollTrigger from "@material-ui/core/useScrollTrigger";
import Container from "@material-ui/core/Container";
import NavBarMakeStyle from "./NavbarMakeStyle";
import Hidden from "@material-ui/core/Hidden";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Box from "@material-ui/core/Box";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import Button from "@material-ui/core/Button";
import Badge from "@material-ui/core/Badge";
// import Button from "@material-ui/core/Button";
import { makeStyles } from "@material-ui/core/styles";
const classes = makeStyles((theme) => ({
  AppBar: {
    backgroundColor: "#ffff",
  },
  logo: {
    marginLeft: 30,
  },
  tabs: {
    marginLeft: "auto",
    fontFamily: "Roboto",
    paddingRight: "30px",
  },
  tabItems: {
    color: "#5d89a2",
    textTransform: "upperCase",
    fontWeight: "300",
    fontSize: "0.90em",
    minWidth: 5,
    marginLeft: "8px",
  },
  tabContainer: {
    marginLeft: "auto",
    marginRight: "100px",
  },
  signButton: {
    backgroundColor: "#04091e",
    textTransform: "none",
    color: "white",
    boxShadow: theme.shadows[0],
    fontSize: "18px",
    paddingLeft: "25px",
    paddingRight: "25px",
    borderRadius: "10px",
    marginLeft: "15px",
    "&:hover": {
      backgroundColor: "#2c7eeb",
    },
  },
}));

function ElevationScroll(props) {
  const { children, window } = props;
  // Note that you normally won't need to set the window ref as useScrollTrigger
  // will default to window.
  // This is only being set here because the demo is in an iframe.
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0,
    target: window ? window() : undefined,
  });

  return React.cloneElement(children, {
    elevation: trigger ? 5 : 0,
  });
}

ElevationScroll.propTypes = {
  children: PropTypes.element.isRequired,
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window: PropTypes.func,
};

export default function ElevateAppBar(props) {
  const classes = NavBarMakeStyle();
  const [value, setValue] = useState(0);

  const handleChange = (e, value) => {
    setValue(value);
  };
  const tabs = [
    { label: "Home", path: "/" },
    { label: "Shop", path: "/shop" },
    { label: "Categories", path: "/Categories" },
    { label: "About", path: "/about" },
    { label: "Contact", path: "/contact" },
  ];

  const NavigationTabs = (
    <Hidden mdDown>
      <Box className={classes.tabContainer}>
        <Tabs
          value={value}
          onChange={handleChange}
          className={classes.tabs}
          indicatorColor="transparent"
        >
          {tabs.map((tabItems) => (
            <Tab
              key={tabItems.label}
              className={classes.tabItems}
              label={tabItems.label}
              // component={Link}
              to={tabItems.path}
            />
          ))}
          <Badge
            color="secondary"
            overlap="circle"
            badgeContent="5"
            invisible={false}
            anchorOrigin={{
              vertical: "top",
              horizontal: "right",
            }}
          >
            <Tab icon={<FavoriteIcon />} className={classes.tabItems} />
          </Badge>
          <Tab icon={<ShoppingCartIcon />} className={classes.tabItems} />
          <Button variant="contained" className={classes.signButton}>
            SignUp/SignIn
          </Button>
        </Tabs>
      </Box>
    </Hidden>
  );

  return (
    <React.Fragment>
      <CssBaseline />
      <ElevationScroll {...props}>
        <AppBar className={classes.AppBar}>
          <Toolbar>
            <img
              src="/logo.png"
              alt="logo"
              width={70}
              height={70}
              className={classes.logo}
            />
            {NavigationTabs}
          </Toolbar>
        </AppBar>
      </ElevationScroll>
      <Toolbar />

      <Container>
        <Box my={2}></Box>
      </Container>
    </React.Fragment>
  );
}
