import { makeStyles } from "@material-ui/core/styles";
const JumbotronMakeStyle = makeStyles((theme) => ({
  container: {
    backgroundColor: "#fff",
  },
  textContent: {
    [theme.breakpoints.down("sm")]: {
      marginLeft: "15px",
    },
  },
  mainText: {
    [theme.breakpoints.down("sm")]: {
      fontSize: "3em",
    },
  },
  paragraphText: {
    [theme.breakpoints.down("sm")]: {
      fontSize: "1em",
    },
  },
}));

export default JumbotronMakeStyle;
