    width: "100%",
    height: "50%",
    marginTop: "-20px",
  },
  jumbotronImage: {
    width: "100%",
    height: "500px",
    [theme.breakpoints.down("sm")]: {
      marginTop: "20px",
      width: "100%",
      height: "300px",
      marginBottom: 0,
    },
  },
  textContainer: {
    marginTop: 100,
    marginRight: 140,
