  },

  mainText: {
    // [theme.breakpoints.down("sm")]: {
    //   fontSize: "3em",
    //   marginTop: 0,
    //   marginBottom: 0,
    //   marginRight: 15,
    //   marginLeft: 15,
    // },
  },
  paragraphText: {
    // [theme.breakpoints.down("sm")]: {
    //   fontSize: "0.9em",
    //   marginTop: 0,
    //   marginBottom: 10,
    //   marginRight: 15,
    //   marginLeft: 20,
    // },
  },
  shopNowButton: {
    // [theme.breakpoints.down("sm")]: {
    //   marginTop: 0,
    //   marginBottom: 10,
    //   marginRight: 15,
    //   marginLeft: 20,
    // },
  },
}));

export default JumbotronMakeStyle;
