import { makeStyles } from "@material-ui/core/styles";
const JumbotronMakeStyle = makeStyles((theme) => ({
  container: {
    backgroundColor: "#fff",
    width: "100%",
    height: "50%",
    marginTop: "-20px",
  },

  // jumbotronImage: {
  //   width: "100%",
  //   height: "500px",
  // },
  jumbotronImage: {
    width: "100%",
    height: "500px",
    [theme.breakpoints.down("sm")]: {
      marginTop: "20px",
      width: "100%",
      height: "300px",
      marginBottom: 0,
    },
    // [theme.breakpoints.down("md")]: {
    //   marginTop: "20px",
    //   width: "100%",
    //   height: "400px",
    // },
  },

  textContainer: {
    marginTop: 100,
    marginRight: 140,
  },

  // later ---->
  textContent: {
    [theme.breakpoints.down("sm")]: {
      marginLeft: "15px",
    },
  },
  mainText: {
    [theme.breakpoints.down("sm")]: {
      fontSize: "3em",
    },
  },
  paragraphText: {
    [theme.breakpoints.down("sm")]: {
      fontSize: "1em",
    },
  },
}));

export default JumbotronMakeStyle;
