import { makeStyles } from "@material-ui/core/styles";
const JumbotronMakeStyle = makeStyles((theme) => ({
  container: {
    backgroundColor: "#fff",
    width: "100%",
    height: "50%",
    marginTop: "-20px",
  },
  jumbotronImage: {
    width: "100%",
    height: "500px",
    [theme.breakpoints.down("sm")]: {
      marginTop: "20px",
      width: "100%",
      height: "300px",
      marginBottom: 0,
    },
  },
  textContainer: {
    marginTop: 100,
    marginRight: 140,
    [theme.breakpoints.down("sm")]: {
      marginTop: "50px",
      marginRight: 0,
      textAlign: "center",
      marginLeft: 0,
      // margin: 20,
    },
  },

  mainText: {
    [theme.breakpoints.down("xs")]: {
      fontSize: "3em",
      marginTop: 0,
      marginBottom: 0,
      marginRight: 15,
      marginLeft: 15,
    },
    [theme.breakpoints.down("sm")]: {
      fontSize: "3em",
      marginTop: 0,
      marginBottom: 0,
      marginRight: 15,
      marginLeft: 50,
    },
  },
  paragraphText: {
    [theme.breakpoints.down("xs")]: {
      fontSize: "0.9em",
      marginTop: 0,
      marginBottom: 10,
      marginRight: 15,
      marginLeft: 20,
    },
  },
}));

export default JumbotronMakeStyle;
