import { makeStyles } from "@material-ui/core/styles";
const JumbotronMakeStyle = makeStyles((theme) => ({
  container: {
    backgroundColor: "#fff",
    width: "100%",
    height: "50%",
    marginTop: "-20px",
  },

  container: {
    [theme.breakpoints.down("sm")]: {
      backgroundColor: "#ffff",
      width: "50%",
      height: "25%",
    },
  },
  textContainer: {
    marginTop: 100,
    marginRight: 140,
  },

  // later ---->
  textContent: {
    [theme.breakpoints.down("sm")]: {
      marginLeft: "15px",
    },
  },
  mainText: {
    [theme.breakpoints.down("sm")]: {
      fontSize: "3em",
    },
  },
  paragraphText: {
    [theme.breakpoints.down("sm")]: {
      fontSize: "1em",
    },
  },
}));

export default JumbotronMakeStyle;
