import { makeStyles } from "@material-ui/core/styles";
const NavBarMakeStyle = makeStyles((theme) => ({
  AppBar: {
    backgroundColor: "#ffff",
    marginBottom: "0px",
  },
  logo: {
    marginLeft: 30,
  },
  logo: {
    [theme.breakpoints.down("sm")]: {
      marginLeft: 7,
    },
  },
  tabs: {
    marginLeft: "auto",
    fontFamily: "Roboto",
    paddingRight: "30px",
  },
  tabItems: {
    color: "#5d89a2",
    textTransform: "upperCase",
    fontWeight: "300",
    fontSize: "0.90em",
    minWidth: 5,
    marginLeft: "8px",
  },
  tabContainer: {
    marginLeft: "auto",
    marginRight: "100px",
    marginTop: "10px",
  },
  signButton: {
    backgroundColor: "#04091e",
    textTransform: "none",
    color: "white",
    boxShadow: theme.shadows[0],
    fontSize: "18px",
    paddingLeft: "25px",
    paddingRight: "25px",
    borderRadius: "10px",
    marginLeft: "15px",
    "&:hover": {
      backgroundColor: "#2c7eeb",
    },
  },
  iconButton: {
    marginLeft: "auto",
    marginTop: "7px",
  },
  menuBar: {
    width: "40px",
    height: "40px",
    color: "#2222",
    "&:hover": {
      backgroundColor: "none",
    },
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1,
    width: "40%",
    border: "none",
    backgroundColor: "#cccc",
    borderRadius: "20px",
    padding: 5,
    paddingLeft: 15,
    fontFamily: "Montserrat",
  },
}));

export default NavBarMakeStyle;
