import { makeStyles } from "@material-ui/core/styles";
import hero from "../../../assets/img/hero/h1_hero.png";
const JumbotronMakeStyle = makeStyles((theme) => ({
  Hero: {
    backgroundImage: `url('/assets/navbarImages/undraw_shopping_app_flsj.png')`,
    backgroundPosition: "center",
    height: "750px",
    width: "100%",
  },
  textContent: {
    [theme.breakpoints.down("sm")]: {
      marginLeft: "15px",
    },
  },
  mainText: {
    [theme.breakpoints.down("sm")]: {
      fontSize: "3em",
    },
  },
  paragraphText: {
    [theme.breakpoints.down("sm")]: {
      fontSize: "1em",
    },
  },
}));

export default JumbotronMakeStyle;
