import React, { useState } from "react";
import Grid from "@material-ui/core/Grid";
import ProductMakeStyle from "./ProductMakeStyle";
import clsx from "clsx";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Collapse from "@material-ui/core/Collapse";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ShareIcon from "@material-ui/icons/Share";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import Rating from "@material-ui/lab/Rating";
const Product = () => {
  const classes = ProductMakeStyle();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };
  return (
    <Grid>
      <Grid item container direction="row" justify="space-between">
        <Grid item md={4} lg={3} sm={6} xs={12}>
          <Card className={classes.root}>
            <CardMedia
              className={classes.media}
              image="/assets/pexels-snapwire-286283.jpg"
              title="Paella dish"
            />
            <CardContent>
              <Typography variant="h4">Product one</Typography>
              <Typography variant="h5">GHc: 70.00</Typography>
              <Typography variant="body2" color="textSecondary" component="p">
                This impressive paella is a perfect party dish and a fun meal to
                cook together with your guests. Add 1 cup of frozen peas along
                with the mussels, if you like.
              </Typography>
            </CardContent>
            <CardContent>
              <Rating name="read-only" value={4} readOnly />
            </CardContent>
            <CardActions>
              <IconButton aria-label="add to favorites">
                <FavoriteIcon />
              </IconButton>
              <IconButton aria-label="addCart">
                <ShoppingCartIcon />
              </IconButton>
              <IconButton aria-label="share">
                <ShareIcon />
              </IconButton>
            </CardActions>
          </Card>
        </Grid>
        <Grid item md={4} lg={3} sm={6} xs={12}>
          <span>2nd Card</span>
        </Grid>
        <Grid item md={4} lg={3} sm={6} xs={12}>
          <span>3rd Card</span>
        </Grid>
        <Grid item md={4} lg={3} sm={6} xs={12}>
          <span>3rd Card</span>
        </Grid>
      </Grid>
    </Grid>
  );
};

export default Product;
