import { makeStyles } from "@material-ui/core/styles";
const JumbotronMakeStyle = makeStyles((theme) => ({
  container: {
    backgroundColor: "#fff",
    width: "100%",
    height: "50%",
    marginTop: "-20px",
  },
  textContainer: {
    margin: 90,
  },
  textContent: {
    [theme.breakpoints.down("sm")]: {
      marginLeft: "15px",
    },
  },
  mainText: {
    [theme.breakpoints.down("sm")]: {
      fontSize: "3em",
    },
  },
  paragraphText: {
    [theme.breakpoints.down("sm")]: {
      fontSize: "1em",
    },
  },
}));

export default JumbotronMakeStyle;
