          </Grid>
          <Grid item>
            <Typography variant="h4">
              paella is a perfect party dish and a fun meal to cook together
              with your guests..
            </Typography>
          </Grid>
          <Grid item>
            <Typography variant="h4">
              paella is a perfect party dish and a fun meal to cook together
              with your guests..
            </Typography>
          </Grid>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Product;
