import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
import CloseIcon from "@material-ui/icons/Close";
import IconButton from "@material-ui/core/IconButton";
import PhotoCamera from "@material-ui/icons/PhotoCamera";
import Avatar from "@material-ui/core/Avatar";
import AvatarGroup from "@material-ui/lab/AvatarGroup";
import CircularProgress from "@material-ui/core/CircularProgress";
import Card from "@material-ui/core/Card";
import Button from "@material-ui/core/Button";
import axios from "axios";
const styles = (theme) => ({
  paper: {
    maxWidth: 936,
    margin: "auto",
    overflow: "hidden",
  },
  searchBar: {
    borderBottom: "1px solid rgba(0, 0, 0, 0.12)",
  },
  searchInput: {
    fontSize: theme.typography.fontSize,
  },
  block: {
    display: "block",
  },
  addUser: {
    marginRight: theme.spacing(1),
  },
  contentWrapper: {
    margin: "40px 16px",
  },
  root: {
    "& > *": {
      margin: theme.spacing(0),
      width: "100%",
      marginBottom: "15px",
    },
  },
  large: {
    width: theme.spacing(20),
    height: theme.spacing(20),
    borderRadius: "30px",
    margin: "10px",
  },
  button: {
    width: "100%",
    padding: "11px",
  },
});
function Content(props) {
  const { classes } = props;
  const [image, setImage] = useState("");
  const [images, setImages] = useState([]);
  const [spinner, setSpinner] = useState(false);
  const [productInput, setProductInput] = useState("");
  const [priceInput, setPriceInput] = useState("");
  const [quantityInput, setQuantityInput] = useState("");
  const [categoryInput, setCategoryInput] = useState("");
  const [tagInput, setTagInput] = useState("");
  const [tag, setTag] = useState([]);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:1000/api/tag/all-tags", {
        headers: {
          "x-auth-token":
            "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNWZlNzBjMjRiNWUxYjgyOGM4YmFkYjA5In0sImlhdCI6MTYxNDI4NjE3MX0.Lj0Mmj5g2yEAYqYVOQtVoMszlWs-1v7EO_BKNT-ZgkI",
        },
      })
      .then((success) => {
        setTag(success.data);
      })
      .catch((e) => {
        console.log(e.response.data.msg);
      });
  }, []);

  // console.log(tag);
  const handleCapture = (e) => {
    setSpinner(true);
    const fileReader = new FileReader();
    if (e.target.files.length === 1) {
      fileReader.onload = (e) => {
        setImage(e.target.result);
        setSpinner(false);
      };
      fileReader.readAsDataURL(e.target.files[0]);
    } else {
      for (var i = 0; i < e.target.files.length; i++) {
        const fileReaders = new FileReader();
        fileReaders.onload = (e) => {
          setImages((oldArr) => [...oldArr, e.target.result]);
          setSpinner(false);
        };
        fileReaders.readAsDataURL(e.target.files[i]);
      }
    }
  };
  const handleInputs = (e, type) => {
    if (type === "productName") {
      setProductInput(e.target.value);
    } else if (type === "priceValue") {
      setPriceInput(e.target.value);
    } else if (type === "quantityValue") {
      setQuantityInput(e.target.value);
    } else if (type === "tagsValue") {
      setTagInput(e.target.value);
    }
    // switch (type) {
    //   case "productName":
    //     setProductInput(e.target.value);
    //   case "priceValue":
    //     setPriceInput(e.target.value);
    //   case "quantityValue":
    //     setQuantityInput(e.target.value);
    //   default:
    //     break;
    // }
  };

  console.log(tagInput);
  return (
    <React.Fragment>
      <form className={classes.root} noValidate autoComplete="off">
        <Typography variant="h5">Add Product</Typography>
        <TextField
          id="outlined-basic"
          label="Add product name"
          variant="outlined"
          value={productInput}
          onChange={(e) => handleInputs(e, "productName")}
        />
        <TextField
          id="outlined-basic"
          label="Add a price"
          variant="outlined"
          type="number"
          value={priceInput}
          onChange={(e) => handleInputs(e, "priceValue")}
        />

        <TextField
          id="outlined-basic"
          label="Add the quantity of product"
          variant="outlined"
          type="number"
          value={quantityInput}
          onChange={(e) => handleInputs(e, "quantityValue")}
        />
        <TextField
          id="outlined-select-currency-native"
          select
          label="Add category"
          SelectProps={{
            native: true,
          }}
          variant="outlined"
        >
          {tag.map((tags, i) => (
            <option
              value={tags.tag}
              onSelect={(e) => handleInputs(e, "tagsValue")}
            >
              {tags.tag}
            </option>
          ))}
        </TextField>

        <TextField
          id="outlined-select-currency-native"
          select
          label="Add tag"
          // value={currency}
          // onChange={handleChange}
          SelectProps={{
            native: true,
          }}
          variant="outlined"
        >
          <option value={10}>Ten</option>
          <option value={20}>Twenty</option>
          <option value={30}>Thirty</option>
        </TextField>
        <input
          accept="image/*"
          style={{ display: "none" }}
          id="icon-button-photo"
          onChange={handleCapture}
          type="file"
          multiple
        />
        <br />
        <label htmlFor="icon-button-photo">
          <Typography variant="body2">
            *Upload a picture(s) of the product. Max: 3 picture*
          </Typography>
          <IconButton color="primary" component="span">
            <PhotoCamera />
          </IconButton>
        </label>

        <br />
        {spinner ? (
          <CircularProgress />
        ) : (
          <AvatarGroup max={3}>
            {image ? (
              <>
                <Avatar alt={image} src={image} className={classes.large} />
                <Card
                  onClick={() => {
                    setImage("");
                  }}
                  style={{
                    backgroundColor: "#616161",
                    width: "30px",
                    height: "30px",
                    borderRadius: "100%",
                    color: "white",
                    textAlign: "center",
                    marginLeft: "-40px",
                    zIndex: "3",
                    paddingTop: "4.8px",
                    cursor: "pointer",
                  }}
                >
                  <CloseIcon />
                </Card>
              </>
            ) : (
              ""
            )}
            {images
              ? images.map((previewImages, i) => (
                  // <img width="50" height="200" src={images[i]} />
                  <>
                    <Avatar
                      alt={images[i]}
                      src={images[i]}
                      className={classes.large}
                    />
                    <Card
                      onClick={() => {
                        const delResult = images.filter(
                          (img) => img != images[i]
                        );
                        setImages(delResult);
                      }}
                      style={{
                        backgroundColor: "#616161",
                        width: "34px",
                        height: "34px",
                        borderRadius: "100%",
                        color: "white",
                        textAlign: "center",
                        marginLeft: "-40px",
                        zIndex: "3",
                        paddingTop: "4.8px",
                        cursor: "pointer",
                      }}
                    >
                      <CloseIcon />
                    </Card>
                    {/* <Button
                      onClick={() => {
                        const delResult = images.filter(
                          (img) => img != images[i]
                        );
                        setImages(delResult);
                      }}
                    >
                      del
                    </Button> */}
                  </>
                ))
              : ""}
          </AvatarGroup>
        )}
      </form>
      <Button
        variant="contained"
        color="primary"
        className={classes.button}
        // onClick={isEditable ? onSubmitHandlerEdit : onSubmitHandler}
        disableRipple={true}
        disabled={true}
      >
        Upload Product
      </Button>
    </React.Fragment>
  );
}

Content.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Content);
