import React, { useState } from "react";
import PropTypes from "prop-types";
import Paper from "@material-ui/core/Paper";
import { withStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableHead from "@material-ui/core/TableHead";
import TableContainer from "@material-ui/core/TableContainer";
import TableRow from "@material-ui/core/TableRow";
import TableCell from "@material-ui/core/TableCell";
import TextField from "@material-ui/core/TextField";

const styles = (theme) => ({
  paper: {
    maxWidth: 936,
    margin: "auto",
    overflow: "hidden",
  },
  searchBar: {
    borderBottom: "1px solid rgba(0, 0, 0, 0.12)",
  },
  searchInput: {
    fontSize: theme.typography.fontSize,
  },
  block: {
    display: "block",
  },
  addUser: {
    marginRight: theme.spacing(1),
  },
  contentWrapper: {
    margin: "40px 16px",
  },
  root: {
    "& > *": {
      margin: theme.spacing(1),
      width: "50%",
      marginBottom: "40px",
    },
  },
});

const createData = (name, tag, category, price, owner, image, search) => {
  return { name, tag, category, price, owner, image, search };
};

function Content(props) {
  const { classes } = props;

  const [rows, setRows] = useState([
    createData("Bag", "Bag", "Fashion", 20.0, "Mends Albert", "img", true),
    createData(
      "Shoes",
      "Shoes",
      "Fashion",
      20.0,
      "Priscilla Mends",
      "img",
      true
    ),
    createData("Jollof", "Rice", "Food", 20.0, "Elvis Mends", "img", true),
  ]);

  const [searchInput, setSearchInput] = useState("");

  const searchHandler = (searchVal) => {
    const rowData = rows
      .map((row) => Object.values(row))
      .filter((options) => options !== true && options !== false);

    const matches = rowData.map((row) =>
      row.map((option) =>
        option.toLowerCase().includes(searchVal.toLowerCase())
      )
    );
    console.log(matches);
  };

  const inputHandler = (e) => {
    setSearchInput(e.target.value);
    searchHandler(e.target.value);
  };

  return (
    <React.Fragment>
      <form className={classes.root} noValidate autoComplete="off">
        <TextField
          id="standard-basic"
          label="Search"
          value={searchInput}
          onChange={(e) => inputHandler(e)}
        />
      </form>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Tag</TableCell>
              <TableCell>Category</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Owner</TableCell>
              <TableCell>Image</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row, index) => (
              <TableRow key={index}>
                <TableCell>{row.name}</TableCell>
                <TableCell>{row.tag}</TableCell>
                <TableCell>{row.category}</TableCell>
                <TableCell>{row.price}</TableCell>
                <TableCell>{row.owner}</TableCell>
                <TableCell>{row.image}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </React.Fragment>
  );
}

Content.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Content);
